mapper.insert:
		insert 
		  into tb_m_schedule
			 (
			   schedule_no
			 , start_date
			 , end_date
			 , start_time
			 , end_time
			 , sc_title
			 , sc_content
			 , status
			 , write_date
			 , member_no
			 , calendar_no
			 , allday
		     ) 
        values
             (
			  seq_sno.nextval
			, #{startDate}
			, #{endDate}
			, #{startTime}
			, #{endTime}
			, #{scTitle}
			, #{scContent}
			, 'Y'
			, SYSDATE
			, #{memberNo}
			, #{calendarNo}
			, #{allday}
		    )

	insert 
      into tb_category
         (
		   calendar_no
		 , my_calendar
		 , sc_color
		 , member_no
	     ) 
	values 
	     (
		   seq_cno.nextval
		 , #{myCalendar}
		 , #{scColor}
		 , #{memberNo}
		 )

mapper.update:
update tb_category
   set
	   my_calendar = #{myCalendar},
	   sc_color = #{scColor}
 where 1=1
   and calendar_no = #{calendarNo}
   and member_no = #{memberNo}

mapper.delete:
delete
  from tb_category
 where calendar_no = #{calendarNo}
   and member_no = #{memberNo}
